function checkFilesUploaded (oldFiles, newFiles) {
  var diff = [];
  _.each(oldFiles, function (oldFile, i) {
    if (oldFile.file !== newFiles[i].file) {
      diff.push(newFiles[i].file);
    }
  });
  return diff;
}

